  #{content,jdbcType=VARCHAR}, #{readNum,jdbcType=DECIMAL}, #{pointNum,jdbcType=DECIMAL},
  #{opened,jdbcType=DECIMAL}, #{top,jdbcType=DECIMAL}, #{articleCat,jdbcType=DECIMAL}, 
  #{ts,jdbcType=TIMESTAMP}, #{creatId,jdbcType=VARCHAR}, #{creatTime,jdbcType=TIMESTAMP}
  )
